import React, { Component } from "react";
import styled from 'styled-components';
import "./Pages.css"

const FormDiv = styled.div`
  margin: 0 auto;
  width: 50%;
`

const FormLabel = styled.label`
  display: block;
`

const FormInput = styled.input`
  width: 100%;
`

const CommentInput = styled.textarea`
  width: 100%;
  height: 6em;
`

// const EmailMe = styled.p`
//   text-align: center;
//   padding: 4%;


class Contact extends Component {

    render() {
      return (
        <div>
          <p>Get in touch with me!</p>

          <FormDiv>
            <form action="michael.j.oconnor@me.com" method="POST" id="contactform">
              <div>
                <FormLabel>Name:</FormLabel>
                <FormInput type="text" name="name" />
              </div>
              <div>
                <FormLabel>Email:</FormLabel>
                <FormInput type="text" name="_replyto" />
              </div>
              <div>
                <FormLabel>Comment:</FormLabel>
                <CommentInput name="comment" />
              </div>
              <button type="submit">Submit</button>
            </form>
          </FormDiv>

          <p>Email me directly at <a href="mailto:sophiaciocca@gmail.com" target="_blank" rel="noreferrer"><strong>sophiaciocca@gmail.com</strong></a>.</p>

          {/* <hr/> */}
          {/* <CenteredHeader><PortButton href="https://drive.google.com/file/d/0B-Wn-csD32OpVmUyVXFVa0JJakE/view" target="_blank">Download My Resume</PortButton></CenteredHeader> */}
        </div>
      );
    }
  }

  export default Contact;
